import data from "./data";



function App() {
  return (
    <div >
      <header >
        <a href="/">Sheco Essential Organics</a>
      </header>
      <main>
        <h1>Featured Products</h1>
        <div className="products">{
          data.pruducts.map(product => (<div className="product" key={product.slug}>
          <a href={`/product/${product.slug}`}>  <img src={product.image} alt={product.name}/></a>

            <div className="product-info">
            <a href={`/product/${product.slug}`}>
            <p>
              {product.name}
            </p>
            </a>
            <p> <strong>
              ${product.price}
              </strong>
            </p>
            </div>
          </div>))
        }</div>

      </main>
    </div>
  );
}

export default App;
