import { Link } from "react-router-dom";
function Product(props){
    const { product } = props;
    return (<Card  key={product.slug}>
    <Link to={`/product/${product.slug}`}>  <img className="card-img-top" src={product.image} alt={product.name}/></Link>
    
      <div className="product-info">
      <Link to={`/product/${product.slug}`}>

      <p>
        {product.name}
      </p>
      </Link>

      <Link to={`/product/${product.slug}`}>
      <p> <strong>
        ${product.price}
        </strong>
      </p>
      </Link>
      <button>Add to cart</button>
      </div>
    </Card>)
}
export default Product;
